#include <bits/stdc++.h>
using namespace std;


void solve(long long N, std::vector<long long> B){
	long long A[N];
	int i;
	for(i=0;i<N;i++){
		if(i==0){
			A[0]=B[0];
		}else if(i==N-1){
			A[N-1] = B[N-2];
		}else{
			A[i] = min(B[i-1],B[i]);
		}
	}
	for(i=0;i<N;i++){
//		cout << A[i] << " ";
//		cout << endl;
	}
	cout << accumulate(A,A+N,0) << endl;

}

// Generated by 1.1.6 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
int main(){
    long long N;
    scanf("%lld",&N);
    std::vector<long long> B(N-1);
    for(int i = 0 ; i < N-1 ; i++){
        scanf("%lld",&B[i]);
    }
    solve(N, std::move(B));
    return 0;
}
